import rc from 'rc';

const defaults = {
  rootPackagePath: false,
  rootPackageJson: false,
  subPackagePaths: false,
  initState: false,
  root: process.cwd(),
  config: false,
  verbose: false,
  help: false,
};

export const config = rc('mrtools', defaults);

if (config.verbose) {
  // tslint:disable-next-line
  console.log(JSON.stringify(config, null, 2));
}

if (config.help) {
  // tslint:disable-next-line
  console.log(`
  mr-tools [OPTION] <root-directory>

  Options
    -v     --verbose    show debugging output
    -h     --help       show this help

  Source: https://github.com/MrRacoon/mr-tools
  `);

  process.exit(0);
}
