import { EmptyAction, PayloadAction, SetRootAction, Type } from "./types";

export function actionCreator<T extends Type>(type: T): EmptyAction<T>;
export function actionCreator<T extends Type, P>(type: T, payload: P): PayloadAction<T, P>;
export function actionCreator<T extends Type, P>(type: T, payload?: P) {
  return payload === undefined ? { type } : { type, payload };
}

export const setRoot = (rootPath: string) => ({
  type: Type.SET_ROOT,
  payload: rootPath,
});
