import { Actions, Model, Type } from "./types";

export default function reducer(model: Model, action: Actions) {
  switch (action.type) {

    case Type.SET_ROOT:
      // Return an updated model, with rootPackage overridden.
      return ({
        ...model,
        rootPackage: action.payload,
      });

    // In all other cases, return the model unchanged.
    default:
      return model;
  }
}
