import { Actions } from './actions';
import { Model, Type } from './types';

export const init: Model = {
  rootPackage: '',
};

export function reducer(model: Model, action: Actions): Model {
  switch (action.type) {

    case Type.SET_ROOT:
      // Return an updated model, with rootPackage overridden.
      return ({
        ...model,
        rootPackage: action.payload,
      });

    // In all other cases, return the model unchanged.
    default:
      return model;
  }
}

export default reducer;
