
// =============================================================================
// Model
//
// Define the shapes our model.
//
export interface Model {
  rootPackage: string;
}

// =============================================================================
// Types
//
// Types identify the redux actions.
//
export enum Type {
  // Set the root of the current mono repo
  SET_ROOT = "SET_ROOT",
  // Actions that we're not concerned with in our reducer
  UNDEFINED = "UNDEFINED",
}

export interface EmptyAction<T extends Type> {
  type: T;
}

export interface PayloadAction<T extends Type, P> {
  type: T;
  payload: P;
}

// =============================================================================
// Actions
//
// Define the shapes our actions.
//

export type SetRootAction = PayloadAction<Type, string>;

export type Actions =
  SetRootAction;
