
export type Name = string;
export type Version = string;
export type Path = string;
export type Dependencies = object;

export interface RootPackageJson {
  name: Name;
  version: Version;
  scripts: object;
  workspaces: Path[];
  dependencies: Dependencies;
}

export interface SubPackageJson {
  name: Name;
  version: Version;
  dependencies?: Name[];
}

export type PackageJson
  = RootPackageJson
  | SubPackageJson;

export interface Scripts {
  lint: string;
  build: string;
  test: string;
}

export interface Package {
  name: Name;
  cwd: Path;
  version: Version;
  scripts: Scripts;
  workspaces?: Path[];
  dependencies?: Dependencies;
}
